class Solution:
    def checkTwoChessboards(self, coordinate1: str, coordinate2: str) -> bool:
        def get_color(coord):
            col = ord(coord[0]) - ord('a') + 1
            row = int(coord[1])
            return (col + row) % 2 == 0
        color1 = get_color(coordinate1)
        color2 = get_color(coordinate2)
        return color1 == color2

