best_solution:
class Solution:
    def minOperations(self, nums: List[int]) -> int:
        if not nums:
            return 0

        operations = 0
        current_value = nums[0]

        for num in nums:
            if num != current_value:
                operations += 1
                current_value = num

        return operations

